import React, { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import logo from './images/logo.png';
import styles from './Chat.module.css';

function Chat() {
  const MySwal = withReactContent(Swal);

  const [tasks, setTasks] = useState([]);
  const [hasTasks, setHasTasks] = useState(false);

  const [newTaskTitle, setNewTaskTitle] = useState('');
  const [newTaskDescription, setNewTaskDescription] = useState('');
  const [newTaskDueDate, setNewTaskDueDate] = useState('');

  const obtenerUsuario = () => {
    if (localStorage.getItem('user') === null || localStorage.getItem('user') === undefined) {
      return 'user';
    } else {
      return localStorage.getItem('user');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    localStorage.removeItem('userId');
  };

  const usuario = obtenerUsuario();

  const handleAddTask = async () => {
    if (!newTaskTitle.trim()) {
      MySwal.fire({
        title: 'Empty Task',
        text: 'Please enter a task title.',
        icon: 'warning',
        buttons: false,
        timer: 3000,
      });
      return;
    }

    try {
      const taskData = {};

      if (newTaskTitle) {
        taskData.title = newTaskTitle;
      }

      if (newTaskDescription) {
        taskData.description = newTaskDescription;
      }

      if (newTaskDueDate) {
        taskData.dueDate = newTaskDueDate;
      }

      const response = await fetch('/tasks', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: localStorage.getItem('userId'),
          taskData: taskData,
        }),
      });

      if (response.ok) {
        const responseData = await response.json();
        const { task } = responseData;

        // Actualizar la lista de tareas en el estado
        setTasks([...tasks, task]);

        // Limpiar los campos de creación de tareas
        setNewTaskTitle('');
        setNewTaskDescription('');
        setNewTaskDueDate('');

        // Mostrar un mensaje de éxito
        MySwal.fire({
          title: 'Task Added',
          text: 'The task has been added successfully.',
          icon: 'success',
          buttons: false,
          timer: 3000,
        });
      } else {
        // Manejo de errores al agregar la tarea
        const errorData = await response.json();
        throw new Error(errorData.error);
      }

    } catch (error) {
      console.error('Error adding task:', error);
      // Mostrar un mensaje de error
      MySwal.fire({
        title: 'Error',
        text: 'An error occurred while adding the task.',
        icon: 'error',
        buttons: false,
        timer: 3000,
      });
    }
  };


  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await fetch(`/tasks/${localStorage.getItem('userId')}`);
        const data = await response.json();
        setTasks(data.tasks);
        setHasTasks(data.tasks.length > 0);
      } catch (error) {
        console.error('Error al obtener las tareas del usuario:', error);
        // Manejo del error al obtener las tareas
      }
    };

    if (usuario === 'user') {
      MySwal.fire({
        title: 'Not Registered',
        text: 'You are not registered. You will be redirected to the registration page.',
        icon: 'warning',
        buttons: false,
        timer: 5000,
      }).then(() => {
        window.location.href = '/register';
      });
    } else {
      fetchTasks();
    }
  }, []);
  if (usuario === 'user') {
    MySwal.fire({
      title: 'Not Registered',
      text: 'You are not registered. You will be redirected to the registration page.',
      icon: 'warning',
      buttons: false,
      timer: 5000,
    }).then(() => {
      window.location.href = '/register';
    });
  }

  return (
    <div className={styles['chat-body']}>
      <header className={styles['chat-header']}>
        <nav className={styles['chat-nav']}>
          <div className={styles['chat-container']}>
            <div className={styles['chat-logo']}>
              <img src={logo} width="60" alt="Logo" />
            </div>
            <h1 className={styles['chat-title']}>Welcome, {usuario}</h1>
            <a onClick={handleLogout} href="/" id="logout">Log Out</a>
          </div>
        </nav>
      </header>

      <main className={styles['chat-main']}>
        <aside className={styles['chat-card']}>
          <div>
            <h2 className={styles['chat-header-task']}>Pending Tasks</h2>
            {hasTasks ? (
              <ul id="taskList">
                {tasks.map(task => (
                  <li key={task.id}>{task.name}</li>
                ))}
              </ul>
            ) : (
              <p>No tasks available</p>
            )}
          </div>
          <div>
            <hr />
            <br />
            <input
              type="text"
              placeholder="Task title"
              value={newTaskTitle}
              onChange={event => setNewTaskTitle(event.target.value)}
              id="newTaskTitle"
            />
<br />
            <textarea
              placeholder="Task description"
              value={newTaskDescription}
              onChange={event => setNewTaskDescription(event.target.value)}
              id="newTaskDescription"
              rows={4}
              style={{ resize: "none", height: "120px" }}
            ></textarea>
            <br />

            <label htmlFor="newTaskDueDate">Due Date:</label>
            <input
              type="date"
              placeholder="Enter due date"
              value={newTaskDueDate}
              onChange={event => setNewTaskDueDate(event.target.value)}
              id="newTaskDueDate"
            />
          </div>

          <div>
            <button onClick={handleAddTask} id="addTask">Add</button>
            <button id="saveTasks">Save</button>
            <button id="notes">Fast notes</button>
          </div>
        </aside>

        <div className={styles['chat']}>
          <div className={styles['chat-header2']}>
            <h2>Chat</h2>
            <button id="reset">Clear chat</button>
          </div>
          <div className={styles['chat-messages']}>
            <div className="message">
              <div className="message-header">
                <span className="username">AI</span>
                <span className="timestamp" id="timestamp"></span>
              </div>
              <div className="message-body">
                <p>Hi, how are you?</p>
              </div>
            </div>
          </div>
          <div className={styles['chat-input']}>
            <input type="text" placeholder="Escribe tu mensaje aquí..." id="message-input" />
            <button id="microphone-button"><i className="fa fa-microphone"></i></button>
            <button id="send-button">Enviar</button>
          </div>
        </div>
      </main>
    </div>
  );
}

export default Chat;
