import React, { useRef } from 'react';

import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';

import './Login.css'; // Ruta al archivo CSS externo
import logo from './images/logo.png'


function Register() {

  const nameRef = useRef();
  const emailRef = useRef();
  const usernameRef = useRef();
  const passwordRef = useRef();

  const handleSubmit = (event) => {
    event.preventDefault();

    const MySwal = withReactContent(Swal);

    // Obtener los valores de los campos
    const name = nameRef.current.value;
    const email = emailRef.current.value;
    const username = usernameRef.current.value;
    const password = passwordRef.current.value;

    // Expresiones regulares para validar los campos
    const nameRegex = /^[a-zA-Z\s]+$/;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const usernameRegex = /^[a-zA-Z0-9_]+$/;
    const passwordRegex = /^[a-zA-Z0-9]{6,}$/;

    if (!nameRegex.test(name)) {
      MySwal.fire({
        icon: 'error',
        title: 'Error',
        text: 'El nombre no cumple con los requisitos. Debe contener solo letras y espacios.',
      });
      return;
    }

    if (!emailRegex.test(email)) {
      MySwal.fire({
        icon: 'error',
        title: 'Error',
        text: 'El correo electrónico no cumple con los requisitos. Debe tener un formato válido.',
      });
      return;
    }

    if (!usernameRegex.test(username)) {
      MySwal.fire({
        icon: 'error',
        title: 'Error',
        text: 'El nombre de usuario no cumple con los requisitos. Debe contener solo letras, números y guiones bajos.',
      });
      return;
    }

    if (!passwordRegex.test(password)) {
      MySwal.fire({
        icon: 'error',
        title: 'Error',
        text: 'La contraseña no cumple con los requisitos. Debe tener al menos 6 caracteres y puede contener letras y números.',
      });
      return;
    }

    // Si todos los campos son válidos, enviar la solicitud al servidor
    const data = {
      name,
      email,
      username,
      password
    };

    fetch('/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
      .then(response => response.json())
      .then(data => {
        // Manejar la respuesta del servidor aquí

        MySwal.fire({
          icon: 'success',
          title: 'Registration Successful',
          text: 'You have successfully registered.',
          timerProgressBar: true,
        });
        setTimeout(function () {
          window.location.href = '/login';
        }, 3000);
      })
      .catch(error => {
        // Manejar cualquier error que ocurra durante la solicitud
        console.error(error);
      });
  };

  return (
    <body>
      <header>
        <nav class="navbar">
          <div class="container">
            <div>
              <img src={logo} width="60" alt="Logo" />
            </div>
            <a href="login">Login</a>
            <a href="/">Home</a>
          </div>
        </nav>
      </header>

      <main>
        <form class="form" onSubmit={handleSubmit}>
          <p id="heading">Register to begin!<i class="fas fa-sign-in-alt"></i></p>
          <div class="field">
            <svg
              class="input-icon"
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              fill="currentColor"
              viewBox="0 0 16 16"
            >
              <path
                d="M13.106 7.222c0-2.967-2.249-5.032-5.482-5.032-3.35 0-5.646 2.318-5.646 5.702 0 3.493 2.235 5.708 5.762 5.708.862 0 1.689-.123 2.304-.335v-.862c-.43.199-1.354.328-2.29.328-2.926 0-4.813-1.88-4.813-4.798 0-2.844 1.921-4.881 4.594-4.881 2.735 0 4.608 1.688 4.608 4.156 0 1.682-.554 2.769-1.416 2.769-.492 0-.772-.28-.772-.76V5.206H8.923v.834h-.11c-.266-.595-.881-.964-1.6-.964-1.4 0-2.378 1.162-2.378 2.823 0 1.737.957 2.906 2.379 2.906.8 0 1.415-.39 1.709-1.087h.11c.081.67.703 1.148 1.503 1.148 1.572 0 2.57-1.415 2.57-3.643zm-7.177.704c0-1.197.54-1.907 1.456-1.907.93 0 1.524.738 1.524 1.907S8.308 9.84 7.371 9.84c-.895 0-1.442-.725-1.442-1.914z"
              ></path>
            </svg>
            <input
              autocomplete="off"
              placeholder="Name"
              class="input-field"
              type="text"
              ref={nameRef}
            />
          </div>
          <div class="field">
            <svg
              class="input-icon"
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              fill="currentColor"
              viewBox="0 0 16 16"
            >
              <path
                d="M13.106 7.222c0-2.967-2.249-5.032-5.482-5.032-3.35 0-5.646 2.318-5.646 5.702 0 3.493 2.235 5.708 5.762 5.708.862 0 1.689-.123 2.304-.335v-.862c-.43.199-1.354.328-2.29.328-2.926 0-4.813-1.88-4.813-4.798 0-2.844 1.921-4.881 4.594-4.881 2.735 0 4.608 1.688 4.608 4.156 0 1.682-.554 2.769-1.416 2.769-.492 0-.772-.28-.772-.76V5.206H8.923v.834h-.11c-.266-.595-.881-.964-1.6-.964-1.4 0-2.378 1.162-2.378 2.823 0 1.737.957 2.906 2.379 2.906.8 0 1.415-.39 1.709-1.087h.11c.081.67.703 1.148 1.503 1.148 1.572 0 2.57-1.415 2.57-3.643zm-7.177.704c0-1.197.54-1.907 1.456-1.907.93 0 1.524.738 1.524 1.907S8.308 9.84 7.371 9.84c-.895 0-1.442-.725-1.442-1.914z"
              ></path>
            </svg>
            <input
              autocomplete="off"
              placeholder="Email"
              class="input-field"
              type="text"
              ref={emailRef}

            />
          </div>
          <div class="field">
            <svg
              class="input-icon"
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              fill="currentColor"
              viewBox="0 0 16 16"
            >
              <path
                d="M13.106 7.222c0-2.967-2.249-5.032-5.482-5.032-3.35 0-5.646 2.318-5.646 5.702 0 3.493 2.235 5.708 5.762 5.708.862 0 1.689-.123 2.304-.335v-.862c-.43.199-1.354.328-2.29.328-2.926 0-4.813-1.88-4.813-4.798 0-2.844 1.921-4.881 4.594-4.881 2.735 0 4.608 1.688 4.608 4.156 0 1.682-.554 2.769-1.416 2.769-.492 0-.772-.28-.772-.76V5.206H8.923v.834h-.11c-.266-.595-.881-.964-1.6-.964-1.4 0-2.378 1.162-2.378 2.823 0 1.737.957 2.906 2.379 2.906.8 0 1.415-.39 1.709-1.087h.11c.081.67.703 1.148 1.503 1.148 1.572 0 2.57-1.415 2.57-3.643zm-7.177.704c0-1.197.54-1.907 1.456-1.907.93 0 1.524.738 1.524 1.907S8.308 9.84 7.371 9.84c-.895 0-1.442-.725-1.442-1.914z"
              ></path>
            </svg>
            <input
              autocomplete="off"
              placeholder="Username"
              class="input-field"
              type="text"
              ref={usernameRef}
            />
          </div>
          <div class="field">
            <svg
              class="input-icon"
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              fill="currentColor"
              viewBox="0 0 16 16"
            >
              <path
                d="M8 1a2 2 0 0 1 2 2v4H6V3a2 2 0 0 1 2-2zm3 6V3a3 3 0 0 0-6 0v4a2 2 0 0 0-2 2v5a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V9a2 2 0 0 0-2-2z"
              ></path>
            </svg>
            <input placeholder="Password" class="input-field" type="password"
              ref={passwordRef} />
          </div>
          <div class="btn"></div>
          <button class="button3">End register</button>
        </form>
      </main>

    </body>
  )
}

export default Register
